let videoIdList=["36yEfPaQcSc","HnYwJzVHaLI","_rZou_PqFoo"];

export default class Example extends React.Component {
    constructor(props) {
        super(props);
      }; 
    render() {
        const opts = {
            height: '390',
            width: '640',
            playerVars: {
                // https://developers.google.com/youtube/player_parameters
                autoplay: 1,
            },
        };
        console.log(videoIdList[1]);
        return (
        <YouTube videoId={videoIdList[2]} opts={opts} onReady={this._onReady} /> 
        );
    }

    _onReady(event) {
        // access to player in all event handlers via event.target
        event.target.pauseVideo();
    }
}  
