import React from "react";
import ReactDOM from "react-dom";
import Rating from "material-ui-rating";
import DopComponent from '../components/DopComponent';
import {
  BrowserRouter as Router,
  Route,
  Link,
  Switch
} from 'react-router-dom';
import Example from "../container/example";

class ListOfProjects extends React.Component {
  constructor(props) {
    super(props);
    this.MouseMove = this.MouseMove.bind(this);
    this.click = this.click.bind(this);
    this.clicko = this.clicko.bind(this);
  }
  MouseMove() {
    ReactDOM.render(
      <div>
        <img src={this.props.photo} onClick={this.clicko} />
        <p>
          {this.props.id}.{this.props.name}{" "}
        </p>
      </div>,
      document.getElementById("filmName")
    );
  }
  click() {
    ReactDOM.render(
      <div>
        <img src={this.props.photo} />
        <p>
          {this.props.id}.{this.props.name} Рeйтинг: {this.props.rayt}
        </p>
          	<Rating value={this.props.rayt} max={5} />
      </div>,
      document.getElementById("filmName")
    );
  }
  clicko() {
        if (this.props.id == 1) { 
            ReactDOM.render( 
            <div>  
            <iframe width="490" height="320" src="https://www.youtube.com/embed/36yEfPaQcSc" frameborder="0" allowfullscreen></iframe>  
            {/* <iframe width="490" height="320" src={this.props.url} frameborder="0" allowfullscreen></iframe> */}
          {/*  <Router>
            <div>
              <ul>
                <li>
                  <Link to="/dop" className="active"><p>Посмортеть фильм</p></Link>
                </li>
              </ul>
              <Switch>
                <Route component={DopComponent} path="/dop" /> 
              </Switch>
            </div>
          </Router> */}
          <p>{this.props.id}.{this.props.name} </p>  
          <p>{this.props.descr}</p>
          <p id='raiting'>Рейтинг: <Rating value={this.props.rayt} max={5} /></p>
          <a href={this.props.vid} target="_blank">Посмотреть фильм "{this.props.name}" на YouTube</a>
            </div>,
            document.getElementById("video")
          ); 
        }
  if (this.props.id == 2) { 
    ReactDOM.render(

    <div>  

     <iframe width="490" height="320" src="https://youtu.be/qcPfI0y7wRU" frameborder="0" allowfullscreen></iframe>  

     {/* <iframe width="490" height="320" src={this.props.url} frameborder="0" allowfullscreen></iframe> */}

   {/*  <Router>
    <div>
      <ul>
        <li>
          <Link to="/dop" className="active"><p>Посмортеть фильм</p></Link>
        </li>
      </ul>

      <Switch>
        <Route component={DopComponent} path="/dop" /> 

      </Switch>
    </div>
  </Router> */}
  <p>{this.props.id}.{this.props.name} </p>  
  <p>{this.props.descr}</p>
  <p id='raiting'>Рейтинг: <Rating value={this.props.rayt} max={5} /></p>
  <a href={this.props.vid} target="_blank">Посмотреть фильм "{this.props.name}" на YouTube</a>
    </div>,
     document.getElementById("video")
  ); 
}
  }
  render() {
    return (
      <div>
        <div id="im">
          <img
            className="photo"
            src={this.props.photo}
            onMouseMove={this.MouseMove}
            onClick={this.clicko}
          />
          <p id = "video" onClick={this.click} style={{ cursor: "pointer" }}>
            {this.props.name}{" "}
          </p>
        </div>
      </div>
    );
  }
}

export default ListOfProjects;
