import React, {Component, Fragment} from 'react';
//import {Field, reduxForm} from 'redux-form';
// import {myInput} from './components/Field/index';
//import {myInput } from "../Field/index";
//import {validate} from '../../Validation';
import {requiredInput, correctInput} from '../../Validation';


 
const myInput = (props) =>{
  const {input, type, placeholder, meta}=props;
  return (
      <Fragment>
      <input {...props.input} type={props.type} placeholder={props.placeholder}/>

      {meta.error &&
      meta.touched &&
      <div>
        {meta.error}
      </div>}
      </Fragment>
  )
}

class LoginForm extends Component {
  render () {
    const {handleSubmit} = this.props;
    return (
      <form className='form' onSubmit={handleSubmit}>
        <Field className='form_title'
         component={myInput}
          name="username"
           //component="input"
          type="text"
          placeholder="Имя поьзователя"
          validate={[requiredInput, correctInput]}
        />
        <Field className='form_title'
         component={myInput}
         //component="input"
          name="password"
          type="password"
          placeholder="Пароль"
          validate={[requiredInput]}
        />
        <Field className='form_title'
          name="confirm-password"
          component={myInput}
          type="password"
          placeholder="Пароль"
          validate={[requiredInput]}
        />
        <button class="form_button" type="submit" label="submit">Вход</button>
      </form>
    );
  }
}


LoginForm = reduxForm ({
  form: 'login',
  validate,
}) (LoginForm); 

export default LoginForm;
