import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import max from './max.png' // relative path to image 
import inter from './inter.png'
import cloud from './cloud.png'
import dead from './dead.png'
import br from './br.png'
import matrix from './matrix.png'
import silicon from './silicon.png'
import xfiles from './xfiles.png'
import ListOfProjects from "./ListOfProjects";
const Films = [
  {
    "id": 1,
    "photo": max,
    "name": "Безумный макс",
    "opisan": "Когда засуха приводит человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и переселить человечество на другую планету.",
    "rayting": "9.1"
  },
  {
    "id": 2,
    "photo": inter,
    "name": "Интерсталлар",
    "rayting": "9.3"
  },
  {
    "id": 3,
    "photo": cloud,
    "name": "Облачный атлас",
    "rayting": "9.4"
  },
  {
    "id": 4,
    "photo": dead,
    "name": "Ходячие мертвецы",
    "rayting": "9.5"
  },
  {
    "id": 5,
    "photo": br,
    "name": "Во все тяжкие",
    "rayting": "9.6"
  },
  {
    "id": 6,
    "photo": matrix,
    "name": "Матрица",
    "rayting": "9.1"
  },
  {
    "id": 7,
    "photo": silicon,
    "name": "Кремневая долина",
    "rayting": "9.4"
  },
  {
    "id": 8,
    "photo": xfiles,
    "name": "Секретные материалы",
    "rayting": "9.3"
  },
  {
    "id": 9,
    "photo": cloud,
    "name": "Облачный атлас",
    "rayting": "9.2"
  }
]
class Nav extends Component { 
    render() { 

        return ( 
          <div className="Nav">
              <div className="header">
                <div>
                  <h2>Фильмы</h2>
                </div>
                <div id="filmName">
                  {Films.map(film => (<li key={film.id}>{film.name}, Рейтинг фильма: {film.rayting}</li>))}
                </div>  
            <div><h2>Афиша фильмов</h2></div>
               </div>
            <div className="films">

              {Films.map((e,i) =>
                  <ListOfProjects 
                  photo={e.photo}
                  name={e.rayting}
                  // eslint-disable-next-line react/jsx-no-duplicate-props
                  name={e.name}
                  key={i}
                  />
            )}
            </div>
            {/* <div>
                <img src={max} alt={"max"}/>, 
                <img src={inter} alt={"inter"}/>, 
                <img src={cloud} alt={"cloud"}/> 
              </div>
              <div>
                <img src={max} alt={"max"}/>, 
                <img src={inter} alt={"inter"}/>, 
                <img src={cloud} alt={"cloud"}/> 
              </div>
              <div>
                <img src={max} alt={"max"}/>, 
                <img src={inter} alt={"inter"}/>, 
                <img src={cloud} alt={"cloud"}/> 
              </div> */}
          </div>
        )  
    }
}
ReactDOM.render(
  <Nav />,
document.getElementById("root")
)
