import {axiosForLoginAndRegistration }from '../axios';
import { call, select,put } from 'redux-saga/effects';
import {loginSelector} from '../selectors/index'
import  { userFail,userSuccess} from '../actions/index';
import  {Users} from '../object'



export function* userRequest (payload) {
    const us = Users.length;
    const UsSuccess = 'Success';
    const UsFail = 'Fail ';

    if (us<=6 ){
      yield put (userSuccess(UsSuccess) )
    } else {
        yield put (userFail(UsFail) )
    }
}


export function* loginRequest(payload) {
  console.log("hello world");
  const a = yield select (loginSelector);
  console.log(a);
  const user = yield call(axiosForLoginAndRegistration, 'https://test-todoist.herokuapp.com/api/tasks',  'get');
  console.log(user);
}
