import React, { Fragment } from 'react';

 const myInput = (props) =>{
    const {input, type, placeholder, meta}=props;
    return (
        <Fragment>
        <input {...props.input} type={props.type} placeholder={props.placeholder}/>

        {meta.error &&
        meta.touched &&
        <div>
          {meta.error}
        </div>}
        </Fragment>
    )
}
export default myInput;
