import logo from './logo.svg';
import React from 'react';
import './App.css';
import DopComponent from './example'
//import ReactDOM from 'react-dom';
import {
  BrowserRouter as Router,
  Route,
  Link,
  Switch
} from 'react-router-dom';

class Home extends React.Component{
  render(){
      return <h2>Главная</h2>;
  }
}
class About extends React.Component{
  render(){
      return <h2>О сайте</h2>;
  }
}
class NotFound extends React.Component{
  render(){
      return <h2>Ресурс не найден</h2>;
  }
}
class Products extends React.Component{
  render(){
      return <div>
                  <h2>Товары</h2>
              </div>;
  }
}
class Nav extends React.Component{
  render(){
      return <nav>
              <Link to="/"  activeClassName="active">Главная</Link>  
              <Link to="/about"  activeClassName="active">О сайте</Link>  
              <Link to="/products" activeClassName="active">Товары</Link>
             </nav>;
  }
}
export default function App() {
  return (

    <Router>
      <div>
        <div><Nav />
        <Switch>
                    <Route exact path="/" component={Home} />
                    <Route path="/about" component={About} />
                    <Route path="/products" component={Products} />
                    <Route component={NotFound} />
        </Switch>
        </div>
        <ul>
          <li>
            <Link to="/dop">Запустить видео</Link>
          </li>
        </ul>

        <Switch>
          <Route component={DopComponent} path="/dop" />
        </Switch>
      </div>
    </Router>
  );
}
