  constructor(props){
    super(props)
    this.state = {
      count:1
    }
  }
incrementCount=()=>{
  this.setState({count: this.state.count+1});
}
decrementCount=()=>{
  this.setState({count: this.state.count-1});
}
  render(){
    const name = 'Alina';
    const { count } = this.state
    return ( <DopComponent 
    helloText = {"My name is " + name}
    incrementClick = {this.incrementCount}
    incrementClickTwo= {this.decrementCount}
    count = {count}
   />
    )
  }
}

