import React, {Component} from 'react';
import {Field, reduxForm} from 'redux-form';
// import {myInput} from './components/Field/index';
import {myInput } from "../Field/index";
import {validate} from '../../Validation';


 


class LoginForm extends Component {
  render () {
    const {handleSubmit} = this.props;
    return (
      <form className='form' onSubmit={handleSubmit}>
        <Field className='form_title'
         component={myInput}
          name="username"
           //component="input"
          type="text"
          placeholder="Имя поьзователя"
        />
        <Field className='form_title'
         component={myInput}
         //component="input"
          name="password"
          type="password"
          placeholder="Пароль"
        />
        <button class="form_button" type="submit" label="submit">Вход</button>
      </form>
    );
  }
}


LoginForm = reduxForm ({
  form: 'login',
  validate,
}) (LoginForm); 

export default LoginForm;
