import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Rating from 'material-ui-rating'
import { Button } from "@material-ui/core";

class ListOfProjects extends Component {
   /*  const hre = "./https://youtu.be/R5KHoE_8dgo"  */ 
    constructor(props) {
        super(props);
        this.MouseMove = this.MouseMove.bind(this);
        this.click = this.click.bind(this);
        this.clicko = this.clicko.bind(this);
    }
    MouseMove(){
        /* console.log(this.props.name); */
        /* alert("Hello React!") */
        ReactDOM.render(
            <div>
              <img  src={this.props.photo} />
              <p>{this.props.id}.{this.props.name} </p>
              
            </div>,
          document.getElementById("filmName")
          )    
        }
        click(){       
              if (this.props.id === 1) {
                <Button
                    variant="contained"
                  /*  href="https://youtu.be/RG_tWoYIQ5Q" */
                  href="show.html"
                    color="secondary"
                  >
                    Посмотреть видос
                  </Button>
             }  
            /*  if ((this.props.id === 2) || (this.props.id === 7) || (this.props.id === 8) || (this.props.id === 9)){
              aa = 4;
           }  
           if ((this.props.id === 3) || (this.props.id === 4)){
            aa = 5;
           }   */
            console.log(this.props.id);  
            /* alert("Hello React!") */
            ReactDOM.render(
                <div>
                 {/*  if (this.props.id === 1) {
                    <Button
                        variant="contained"
                       href="https://youtu.be/RG_tWoYIQ5Q" 
                      href="show.html"
                        color="secondary"
                      >
                        Посмотреть видос
                      </Button>
                  } */}
                  <img  src={this.props.photo} />
                  <p>{this.props.id}.{this.props.name} Рэйтинг: {this.props.rayt}</p> 
                  <Rating value={this.props.rayt} max={5} />   
                </div>,
              document.getElementById("filmName")
              )    
            }
            
    render() {
        
        return (
            <div>
                    <div id="im">
                          
                           {/*  <a
                                className="App-link"
                                href="https://youtu.be/R5KHoE_8dgo" 
                                target="_blank"
                                rel="noopener noreferrer"
                            > */}
                            <img className="foto" src={this.props.photo} onMouseMove={this.MouseMove} onClick={this.click}/>
                            {/* </a> */}
                            
                          <p onClick={this.click} style={{cursor: "pointer"}}>{this.props.name} </p>  
                    </div>
            </div>
        );
    }

}

export default ListOfProjects;
