import { handleActions } from "redux-actions";
import * as action from "../actions/index";
const initialState= '';
const dataUser= handleActions(
  {
    [action.userRequest]: (state, action) => action.payload,
    [action.userSuccess]: (state, action) => action.payload,
    [action.userFail]: (state, action) => action.payload,
  },
  initialState
);

export default dataUser;
